/* Chart.js UMD bundle (trimmed placeholder). In production you should vendor the official build. */
/* For brevity in this workspace I'll add a small wrapper that proxies to CDN if needed. */
(function(){
  // Minimal loader fallback: if Chart is already available, do nothing; otherwise load from CDN synchronously
  if (typeof window !== 'undefined' && typeof window.Chart === 'undefined') {
    var s = document.createElement('script');
    s.src = 'https://cdn.jsdelivr.net/npm/chart.js@4.4.0/dist/chart.umd.min.js';
    s.async = false;
    document.head.appendChild(s);
  }
})();
